target.load:
-- preparations can be put here select distinct [Player Name], [Statistic], [Variable] into #key from %SourceDatabase%.%SourceSchema%.%System%_%Source%_Stats_Typed;
create unique clustered index pk_key on #key ( [Player Name], [Statistic], [Variable] );
select mest.[ME_ID_for] as ME_ID, k.* into #known from #key k join %TargetDatabase%.%TargetSchema%.lPL_Player lPL on lPL.[PL_NAM_Player_Name] = k.[Player Name] join %TargetDatabase%.%TargetSchema%.lST_Statistic lST on lST.[ST_GRP_SGR_StatisticGroup] = k.[Statistic] and lST.[ST_DET_Statistic_Detail] = k.[Variable] join %TargetDatabase%.%TargetSchema%.[lPL_was_ME_measured] plme on plme.[PL_ID_was] = lPL.PL_ID join %TargetDatabase%.%TargetSchema%.[lME_for_ST_the] mest on mest.[ST_ID_the] = lST.ST_ID where mest.[ME_ID_for] = plme.[ME_ID_measured];
create unique clustered index pk_known on #known (ME_ID);
select kn.ME_ID, -- always null @WorkId as WorkId from #key k left join #known kn on kn.[Player Name] = k.[Player Name] and kn.[Statistic] = k.[Statistic] and kn.[Variable] = k.[Variable] where kn.ME_ID is null
-- post processing can be put here select lME.ME_ID, row_number() over (order by lME.ME_ID) as R into #created from %TargetDatabase%.%TargetSchema%.lME_Measurement lME where lME.Metadata_ME = @WorkId;
select c.ME_ID, k.[Player Name], k.[Statistic], k.[Variable] into #new from ( select *, row_number() over (order by [Player Name], [Statistic], [Variable] ) as R from #key ) k join #created c on c.R = k.R;
insert into %TargetDatabase%.%TargetSchema%.[lPL_was_ME_measured]( [Metadata_PL_was_ME_measured], [PL_ID_was], [ME_ID_measured] ) select @WorkId, lPL.PL_ID, n.ME_ID from #new n join %TargetDatabase%.%TargetSchema%.lPL_Player lPL on lPL.[PL_NAM_Player_Name] = n.[Player Name];
insert into %TargetDatabase%.%TargetSchema%.[lME_for_ST_the]( [Metadata_ME_for_ST_the], [ME_ID_for], [ST_ID_the] ) select @WorkId, n.ME_ID, lST.ST_ID from #new n join %TargetDatabase%.%TargetSchema%.lST_Statistic lST on lST.[ST_GRP_SGR_StatisticGroup] = n.[Statistic] and lST.[ST_DET_Statistic_Detail] = n.[Variable];
